INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (1, '', 'You are trained in pleasant social interactions.', 0, 1, 'APPEALING');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (2, 'Resistant to Charms', 'You\'re aware of how others can manipulate and charm people, and you notice when those tactics are used on you. Because of this awareness, you are trained in resisting any kind of persuasion or seduction if you wish it.', 0, 0, 'APPEALING');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (3, '', 'You are trained in initiative', 0, 1, 'BRASH');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (4, 'Bold', 'You are trained in all actions that involve overcoming or ignoring the effects of fear or intimidation.', 0, 0, 'BRASH');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (5, '', 'You\'re trained in all interactions involving lies or trickery', 0, 1, 'CLEVER');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (6, '', 'You\'re trained in defense rolls to resist mental effects', 0, 1, 'CLEVER');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (7, '', 'You\'re trained in all tasks involving, identifying, or assessing danger, lies, quality, importance, function, or power', 0, 1, 'CLEVER');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (8, '', 'You were never good at studying or retaining trivial knowledge. The difficulty of any task involving lore, knowledge, or understanding is increased by one step', 1, 0, 'CLEVER');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (9, '', ' You are trained in running', 0, 1, 'FAST');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (10, 'Fast', 'You can move a short distance and still take another action in the same round, or you can move a long distance as your action without needing to make any kind of roll. ', 0, 0, 'FAST');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (11, '', 'You\'re a sprinter, not a longdistance runner. You don\'t have a lot of stamina. The difficulty of any Might defense roll is increased by one step ', 1, 0, 'FAST');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (12, '', 'You\'re trained in all tasks involving balance and careful movement', 0, 1, 'GRACEFUL');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (13, '', 'You\'re trained in all tasks involving physical performing arts.', 0, 1, 'GRACEFUL');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (14, '', 'You\'re trained in all Speed defense tasks.', 0, 1, 'GRACEFUL');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (15, '', 'You\'re trained in an area of knowledge of your choice.', 0, 1, 'INTELLIGENT');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (16, '', 'You\'re trained in all actions that involve remembering or memorizing things you experience directly. For example, instead of being good at recalling details of geography that you read about in a book, you can remember a path through a set of tunnels that you\'ve explored before.', 0, 1, 'INTELLIGENT');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (17, 'Luck Pool', 'You have one additional Pool called Luck that begins with 3 points, and it has a maximum value of 3 points. When spending points from any other Pool, you can take one, some, or all the points from your Luck Pool first. When you make a recovery roll, your Luck Pool is one additional Pool to which you can add recovered points. When your Luck Pool is at 0 points, it does not count against your damage track. Enabler.', 0, 0, 'LUCKY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (18, 'Advantage', 'When you use 1 XP to reroll a d20 for any roll that affects only you, add +3 to the reroll.', 0, 0, 'LUCKY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (19, '', 'You’re trained in initiative actions', 0, 1, 'SHARP-EYED');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (20, '', 'You’re trained in perception actions.', 0, 1, 'SHARP-EYED');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (21, 'Find the Flaw', 'If an opponent has a straightforward weakness (takes extra damage from fire, can\'t see out of his left eye, and so on), the GM will tell you what it is. ', 0, 0, 'SHARP-EYED');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (22, '', 'You’re trained in identifying.', 0, 1, 'SKEPTICAL');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (23, '', 'You\'re trained in all actions that involve seeing through a trick, illusion, rhetorical ruse designed to evade the issue, or lie. For example, you\'re better at keeping your eye on the cup containing the hidden ball, sensing an illusion, or realizing if someone is lying to you (but only if you specifically concentrate and use this skill).', 0, 1, 'SKEPTICAL');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (24, '', 'You’re trained in all stealth tasks.', 0, 1, 'STEALTHY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (25, '', 'You\'re trained in all interactions involving lies or trickery.', 0, 1, 'STEALTHY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (26, '', 'You\'re trained in all special abilities involving illusions or trickery', 0, 1, 'STEALTHY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (27, '', 'You\'re sneaky but not fast. The difficulty of all movement-related tasks is one step higher for you. ', 1, 0, 'STEALTHY');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (28, '', 'You\'re trained in all actions that involve recognizing and understanding the Strange, its effects, and its denizens, including identifying translated visitors from alternate recursions, as well as identifying and understanding cyphers. Alternatively, you\'re trained in either fractal surfing or Chaosphere navigation. Whichever skill you choose, it grants you a –1 buffer against positive factors of alienation for being exposed to the Strange. ', 0, 1, 'STRANGE');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (29, 'Sense Something Strange', 'You can sense whether creatures of an alternative recursion, creatures of the Strange, Strange cyphers, or other related phenomena are active in situations where their presence is not obvious. You must study an object or location closely for a minute to get a feel for the situation.', 0, 0, 'STRANGE');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (30, '', 'Because you often seem as strange to others as they do to you, people have a harder time interacting with you, sometimes to the point where they can\'t recall your name. The difficulty of any task involving charm, persuasion, etiquette, or deception is increased by one step.', 1, 0, 'STRANGE');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (31, '', 'You\'re trained in all actions involving breaking inanimate objects', 0, 1, 'STRONG');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (32, '', 'You\'re trained in all jumping actions.', 0, 1, 'STRONG');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (33, 'Additional Equipment', 'You have an extra medium or heavy melee weapon', 0, 0, 'STRONG');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (34, 'Healthy', 'Add 1 to the points you regain when you make a recovery roll.', 0, 0, 'TOUGH');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (35, '', 'You’re trained in Might defense actions.', 0, 1, 'TOUGH');
INSERT INTO `TheStrange`.`habilidadDescriptor` (`id`, `nombre`, `descripcion`, `incapacidad`, `habilidad`, `Descriptor_nombre`) VALUES (36, 'Additional Equipment', 'You gain an extra light weapon whenever you translate to a different recursion.', 0, 0, 'TOUGH');
INSERT INTO `TheStrange`.`Clase` (`nombre`, `traslacion`, `minimoDispositivos`, `descripcion`, `vinculos`) VALUES ('Vector', ' easing', 2, ' Vectors are action-oriented people: athletes, soldiers, firefighters, hunters, and explorers. They overcome challenges in a straightforward, physical way. When something needs doing, they do it. They are characterized as much by their motivation as their physical talents. Vectors have great strength, stamina, and speed. It may seem that this is the sum total of the source of their abilities, but eventually they learn that in some small way, these abilities also originate with a connection to the Strange. These abilities—called moves—allow them to perform feats that normal people cannot do. Most vectors frequently train their bodies hard, and they have a background involving intense physical activity: sports, combat, occupations that involve action and exertion as well as skill. Vectors can be mountain climbers, rescue workers, and anything else of a similar vein.', '1_You were a star high school athlete. You\'re still in great shape, but those were the glory days, man./2_Your brother is the lead singer in a really popular band./3_You went to the Olympics and took home a silver medal and two bronze medals./4_You were a cop, but you gave it up after encountering corruption on the force./5_Your parents worked for the Peace Corps, so you spent much of your young life traveling the world./6_You served in the military with honor./7_You received a scholarship from the September Project foundation, which paid for your schooling. Now they seem to want a lot more from you./8_You went to a prestigious university on an athletic scholarship, but you excelled in class as well as on the field./9_Your college roommate is now a member of the United States House of Representatives./10_You used to be a middle school teacher and an athletic coach. Your students remember you fondly./11_You worked as a small-time operative for the mob until you were caught and served some time in jail, after which you tried to go straight./12_You owe money to a number of people and don\'t have the funds to pay your debts./13_An organization called the Estate has been attempting to recruit you for some time, but you\'ve put them off./14_You were kidnapped as a small child under mysterious circumstances, although you were recovered safely. The case still has some notoriety./15_You were a young, professional athlete caught in a doping scandal. Your career ended in shame./16_While working on an oil rig in the Pacific, you saw some strange lights come up out of the sea and fly away./17_You own your own small health food restaurant./18_You were a DJ at a local radio station./19_Your sister is the owner of a sporting goods store and gives you a hefty discount./20_Your father is a colonel in the U.S. Marine Corps with many connections.');
INSERT INTO `TheStrange`.`Clase` (`nombre`, `traslacion`, `minimoDispositivos`, `descripcion`, `vinculos`) VALUES ('Paradox', 'initiating', 3, ' Paradoxes are the mad scientists, the sorcerers, and the breakers of the rules of reality. They are not bound by what others believe to be true. Paradoxes derive their abilities from a connection to the Strange, regardless of where they claim their power originates. With those abilities—called revisions—they can transgress the laws of reality and alter the rules of a recursion. The Strange permeates the cosmos, though it’s hard for anyone not quickened to its Brash, page 46 Looks for Trouble, page 68 Medium weapon, page 86 CREATING YOUR CHARACTER 2 31 CHARACTER TYPE presence to interact with it like paradoxes (and spinners and vectors, in their own ways) do. Though most paradoxes refer to their special abilities as revisions, some refer to them as knacks, prayers, spells, or psychic gifts. Many paradoxes come from a background that includes extensive training of a scientific, technical, religious, or spiritual nature. They could also have experience in an esoteric order. As a result, they have an aptitude with equipment, paraphernalia, or rituals that may aid them in their pursuits.', '1_When you were mugged, you killed the mugger. Now you\'re trying to stay one step ahead of the police detectives who are trying to solve the murder./2_An experiment you conducted in your garage blew up, caused your home to burn down, and killed some of your family. You still dream about it sometimes./3_You once belonged to a monastery. When the others learned of your abilities, some thought you were touched by the divine. Others did not./4_You gave a homeless person enough to stay inside during the course of a brutal storm. That person now owes you a great favor./5_Your mother was a member of the Quiet Cabal before she died, where she was respected by many. Those who knew her are fond of you, but they also expect great things from you./6_Your father was a politician who left office in some disrepute, and you never learned the full story about it./7_You were a code-breaker for the military until you decoded a message that implicated your superior in something foul. Rather than face her, you got yourself dishonorably discharged./8_You revised the world in a public place and were noticed. You\'ve gained notoriety, as well as death threats from religious fanatics./9_You studied at one of the most prestigious schools in the world, but you got in by cheating./10_You headed a small church, and though you left to pursue your own strange journey, the congregation still tries to lure you back to the pulpit./11_You were a night guard before you gained the ability to revise reality./12_You had a successful career writing stirring greeting cards, but you were let go after one of your cards caused a rash of suicides./13_You hacked websites for cash until you crossed the Estate, which sent agents to locate you. When they brought you in, you agreed to join them./14_You were an anthropologist who found something strange in a burial site-something you published, though it got you thrown from the ranks of respectable researchers./15_When you were an envoy working overseas, you made friends with several foreign diplomats, some of whom you still contact./16_You worked for a famous psychic, though she threw you out when she realized your powers were real, as opposed to her tricks./17_You made a mint online with digital currency, but thanks to a computer crash, you lost everything. You\'re determined to build up your nest egg again./18_As a political refugee, you are often treated with suspicion./19_You are fit as a fiddle, but most would think of you as obese./20_Your dog can talk because it is native to a recursion where such an ability isn\'t unusual, though it was a shock to you when you first found ou');
INSERT INTO `TheStrange`.`Clase` (`nombre`, `traslacion`, `minimoDispositivos`, `descripcion`, `vinculos`) VALUES ('Spinner', 'hastening ', 2, ' Spinners are striking individuals, and they possess a personality that allows them to spin tales, spin lies, or spin a version of the truth that makes others see things in a whole new way. If anyone is going to make a friend of an enemy, bluff a way into a high-security compound, or mislead a world-devouring planetovore, it’s a spinner. Whether through leadership, influence, or behind-the-scenes manipulation, a spinner pushes things in the direction she wants them to go. Some spinner abilities could be attained through practice by anyone with some talent, but a spinner has an underlying facility: everything a spinner accomplishes is quickened by her connection to the Strange. A spinner’s abilities—called twists—can be used to adjust, distort, and exploit.', '1_One of your parents was once a famous entertainer in his or her early years and hoped you would excel in the same medium./2_When you were a teenager, one of your siblings went missing and is presumed dead. The shock rent your family, and it’s something you’ve never gotten over./3_You were inducted into a secret society that claims to hold and protect esoteric knowledge opposing the forces of evil./4_You lost one of your parents to alcoholism. He or she may still be alive, but you’d be hard pressed to find forgiveness./5_You have no memory of anything that happened to you before the age of 18./6_Your grandparents raised you on a dairy farm far from bustling urban centers. You like to think the homeschooling you received prepared you for anything./7_As an orphan, you had a difficult childhood, and your entry into adulthood was challenging./8_You grew up on the reservation. When you left, most of your friends thought you were abandoning the tribe./9_As a child actor, you were loved and adored. As an adult, less so./10_You have an annoying rival who always seems to get in your way or foil your plans./11_You’ve worked yourself into the position of spokesperson for a medium-sized company./12_Your next-door neighbors were murdered, and the mystery remains unsolved./13_You have traveled all over the world, and during that time you accumulated quite a collection of strange souvenirs./14_Your high-school sweetheart ended up with your best friend. Now ex-best friend./15_You are part of a maligned minority, but you work to bring the injustice of your status to public attention./16_You’re part-owner of a local bar, where you’re something of a whiz in creating specialty cocktails./17_You once talked an expensive watch off a powerful executive, but in so doing, you earned the executive’s enmity./18_You used to act in a traveling theater, and they remember you fondly (as do people in the places you visited)./19_You won an Aspiring Writers of Tomorrow contest for a short story you wrote, and now you have a couple of offers from publishers to write a novel./20_Someone stole your identity, drained your bank accounts, and is wanted in connection to a series of violent crimes.');
