INSERT INTO `TheStrange`.`Poderes` (`id`, `nombre`, `descripcion`, `coste`, `rango`, `proeza`, `Clase_nombre`) VALUES (40, 'Manipulator', 'You are trained in deceiving, persuading, or intimidating (choose one).', , 1, 0, 'Spinner');
INSERT INTO `TheStrange`.`Poderes` (`id`, `nombre`, `descripcion`, `coste`, `rango`, `proeza`, `Clase_nombre`) VALUES (41, 'Twists', 'You manipulate things, including people, machines, organizations, objects- anything and everything. Some twists are constant, ongoing effects, and others are specific actions that usually cost points from one of your stat Pools. Choose two of the twists described below. You can\'t choose the same twist more than once unless its description says otherwise.Ã§', , 1, 0, 'Spinner');
